@ExtendWith(MockitoExtension.class)
class OrderServiceTest {

    @Mock
    private OrderRepository orderRepository;

    @Mock
    private UserClient userClient;

    @Mock
    private ProductClient productClient;

    @Mock
    private OrderMapper orderMapper;

    @Mock
    private OrderItemMapper orderItemMapper;

    @InjectMocks
    private OrderService orderService;

    private User user;
    private Product product1;
    private Product product2;

    @BeforeEach
    void setUp() {
        user = new User();
        user.setId(100L);
        user.setName("John Doe");

        product1 = new Product();
        product1.setId(200L);
        product1.setPrice(BigDecimal.valueOf(50));

        product2 = new Product();
        product2.setId(201L);
        product2.setPrice(BigDecimal.valueOf(100));
    }

    @Test
    void testFindByIdReturnNullWhenOrderNotFound() {
        when(orderRepository.findByIdWithItems(1L)).thenReturn(Optional.empty());

        Order result = orderService.findById(1L);

        assertNull(result);
        verify(orderRepository).findByIdWithItems(1L);
        verifyNoInteractions(userClient, productClient);
    }

    @Test
    void testFindByIdReturnsOrderSuccessfully() {
        Long orderId = 1L;

        OrderEntity orderEntity = new OrderEntity();
        orderEntity.setId(orderId);
        orderEntity.setUserId(user.getId());
        orderEntity.setOrderNumber("ORD-2024-001");

        OrderItemEntity itemEntity = new OrderItemEntity();
        itemEntity.setProductId(product1.getId());
        itemEntity.setQuantity(2);

        orderEntity.setItems(new HashSet<>(Collections.singletonList(itemEntity)));

        Order order = new Order();
        order.setId(orderId);

        OrderItem orderItem = new OrderItem();
        orderItem.setProductId(product1.getId());
        orderItem.setQuantity(2);

        when(orderRepository.findByIdWithItems(orderId)).thenReturn(Optional.of(orderEntity));
        when(userClient.getUserById(user.getId())).thenReturn(user);
        when(productClient.getProductById(product1.getId())).thenReturn(product1);
        when(orderMapper.toDomainWithUser(orderEntity, user)).thenReturn(order);
        when(orderItemMapper.toDomainWithProduct(itemEntity, product1)).thenReturn(orderItem);

        Order result = orderService.findById(orderId);

        assertNotNull(result);
        assertEquals("ORD-2024-001", result.getOrderNumber());
        assertEquals(1, result.getItems().size());
        verify(productClient, times(1)).getProductById(product1.getId());
    }

    @Test
    void testFindByIdMultipleItems() {
        Long orderId = 1L;

        OrderEntity orderEntity = new OrderEntity();
        orderEntity.setId(orderId);
        orderEntity.setUserId(user.getId());
        orderEntity.setOrderNumber("ORD-2024-001");

        OrderItemEntity item1 = new OrderItemEntity();
        item1.setProductId(product1.getId());
        item1.setQuantity(1);
        OrderItemEntity item2 = new OrderItemEntity();
        item2.setProductId(product2.getId());
        item2.setQuantity(3);

        orderEntity.setItems(new HashSet<>(Arrays.asList(item1, item2)));

        Order order = new Order();
        order.setId(orderId);

        OrderItem orderItem1 = new OrderItem();
        OrderItem orderItem2 = new OrderItem();

        when(orderRepository.findByIdWithItems(orderId)).thenReturn(Optional.of(orderEntity));
        when(userClient.getUserById(user.getId())).thenReturn(user);
        when(productClient.getProductById(product1.getId())).thenReturn(product1);
        when(productClient.getProductById(product2.getId())).thenReturn(product2);
        when(orderMapper.toDomainWithUser(orderEntity, user)).thenReturn(order);
        when(orderItemMapper.toDomainWithProduct(item1, product1)).thenReturn(orderItem1);
        when(orderItemMapper.toDomainWithProduct(item2, product2)).thenReturn(orderItem2);

        Order result = orderService.findById(orderId);

        assertNotNull(result);
        assertEquals(2, result.getItems().size(), "Debe devolver 2 items");
        verify(productClient, times(2)).getProductById(anyLong());
    }

    @Test
    void testRegisterOrderSuccess() {
        CreateOrderRequest request = new CreateOrderRequest();
        request.setUserId(user.getId());

        CreateOrderRequest.Item itemRequest = new CreateOrderRequest.Item();
        itemRequest.setProductId(product1.getId());
        itemRequest.setQuantity(2);

        request.setItems(new HashSet<>(Collections.singletonList(itemRequest)));

        OrderEntity savedEntity = new OrderEntity();
        savedEntity.setId(1L);
        savedEntity.setUserId(user.getId());
        savedEntity.setOrderNumber("ORD-2024-010");

        OrderItemEntity itemEntity = new OrderItemEntity();
        itemEntity.setProductId(product1.getId());
        itemEntity.setQuantity(2);
        savedEntity.setItems(new HashSet<>(Collections.singletonList(itemEntity)));

        Order order = new Order();
        order.setId(savedEntity.getId());

        OrderItem orderItem = new OrderItem();
        orderItem.setProductId(product1.getId());
        orderItem.setQuantity(2);

        when(productClient.getProductById(product1.getId())).thenReturn(product1);
        when(orderRepository.save(any(OrderEntity.class))).thenReturn(savedEntity);
        when(orderMapper.toDomain(savedEntity)).thenReturn(order);
        when(orderItemMapper.toDomainWithProduct(itemEntity, product1)).thenReturn(orderItem);
        when(userClient.getUserById(user.getId())).thenReturn(user);

        Order result = orderService.register(request);

        assertNotNull(result);
        assertEquals("ORD-2024-010", result.getOrderNumber());
        assertEquals(1, result.getItems().size());
        verify(productClient, times(1)).getProductById(product1.getId());
    }
}

