import static org.mockito.Mockito.*;

@ExtendWith(MockitoExtension.class)
class OrderServiceTest {

    @Mock
    private OrderRepository orderRepository;

    @Mock
    private UserClient userClient;

    @Mock
    private ProductClient productClient;

    @Mock
    private OrderMapper orderMapper;

    @Mock
    private OrderItemMapper orderItemMapper;

    @InjectMocks
    private OrderService orderService;

    private User user;
    private Product product1;
    private Product product2;
    private OrderItemEntity itemEntity1;
    private OrderItemEntity itemEntity2;
    private OrderEntity orderEntity;

    @BeforeEach
    void setUp() {
        user = new User();
        user.setId(100L);
        user.setName("John Doe");

        product1 = new Product();
        product1.setId(200L);
        product1.setName("Product 1");
        product1.setPrice(BigDecimal.valueOf(50));

        product2 = new Product();
        product2.setId(201L);
        product2.setName("Product 2");
        product2.setPrice(BigDecimal.valueOf(30));

        itemEntity1 = new OrderItemEntity();
        itemEntity1.setProductId(product1.getId());
        itemEntity1.setQuantity(2);
        itemEntity1.setUnitPrice(product1.getPrice());
        itemEntity1.setSubtotal(product1.getPrice().multiply(BigDecimal.valueOf(itemEntity1.getQuantity())));

        itemEntity2 = new OrderItemEntity();
        itemEntity2.setProductId(product2.getId());
        itemEntity2.setQuantity(1);
        itemEntity2.setUnitPrice(product2.getPrice());
        itemEntity2.setSubtotal(product2.getPrice().multiply(BigDecimal.valueOf(itemEntity2.getQuantity())));

        orderEntity = new OrderEntity();
        orderEntity.setId(1L);
        orderEntity.setOrderNumber("ORD-2024-001");
        orderEntity.setUserId(user.getId());
        orderEntity.setItems(new HashSet<>(Arrays.asList(itemEntity1, itemEntity2)));
    }

    @Test
    void testFindByIdReturnNullWhenOrderNotFound() {
        when(orderRepository.findByIdWithItems(1L)).thenReturn(Optional.empty());

        Order result = orderService.findById(1L);

        assertNull(result);
        verify(orderRepository).findByIdWithItems(1L);
        verifyNoInteractions(userClient, productClient, orderMapper, orderItemMapper);
    }

    @Test
    void testFindByIdReturnsOrderSuccessfully() {
        when(orderRepository.findByIdWithItems(1L)).thenReturn(Optional.of(orderEntity));
        when(userClient.getUserById(user.getId())).thenReturn(user);
        lenient().when(productClient.getProductById(product1.getId())).thenReturn(product1);
        lenient().when(productClient.getProductById(product2.getId())).thenReturn(product2);

        Order order = new Order();
        order.setId(orderEntity.getId());
        order.setOrderNumber(orderEntity.getOrderNumber());

        OrderItem orderItem1 = new OrderItem();
        orderItem1.setProductId(product1.getId());
        OrderItem orderItem2 = new OrderItem();
        orderItem2.setProductId(product2.getId());

        when(orderMapper.toDomainWithUser(orderEntity, user)).thenReturn(order);
        lenient().when(orderItemMapper.toDomainWithProduct(itemEntity1, product1)).thenReturn(orderItem1);
        lenient().when(orderItemMapper.toDomainWithProduct(itemEntity2, product2)).thenReturn(orderItem2);

        Order result = orderService.findById(1L);

        assertNotNull(result);
        assertEquals("ORD-2024-001", result.getOrderNumber());
        assertEquals(2, result.getItems().size());
        verify(orderRepository).findByIdWithItems(1L);
        verify(userClient).getUserById(user.getId());
        verify(orderMapper).toDomainWithUser(orderEntity, user);
    }

    @Test
    void testFindByIdMultipleItems() {
        when(orderRepository.findByIdWithItems(1L)).thenReturn(Optional.of(orderEntity));
        when(userClient.getUserById(user.getId())).thenReturn(user);
        lenient().when(productClient.getProductById(product1.getId())).thenReturn(product1);
        lenient().when(productClient.getProductById(product2.getId())).thenReturn(product2);

        Order order = new Order();
        order.setId(orderEntity.getId());
        order.setOrderNumber(orderEntity.getOrderNumber());

        OrderItem orderItem1 = new OrderItem();
        orderItem1.setProductId(product1.getId());
        OrderItem orderItem2 = new OrderItem();
        orderItem2.setProductId(product2.getId());

        when(orderMapper.toDomainWithUser(orderEntity, user)).thenReturn(order);
        lenient().when(orderItemMapper.toDomainWithProduct(itemEntity1, product1)).thenReturn(orderItem1);
        lenient().when(orderItemMapper.toDomainWithProduct(itemEntity2, product2)).thenReturn(orderItem2);

        Order result = orderService.findById(1L);

        assertNotNull(result);
        assertEquals(2, result.getItems().size(), "Debe devolver 2 items");
    }

    @Test
    void testRegisterOrderSuccess() {
        CreateOrderRequest request = new CreateOrderRequest();
        request.setUserId(user.getId());

        CreateOrderRequest.Item item1Req = new CreateOrderRequest.Item();
        item1Req.setProductId(product1.getId());
        item1Req.setQuantity(2);

        CreateOrderRequest.Item item2Req = new CreateOrderRequest.Item();
        item2Req.setProductId(product2.getId());
        item2Req.setQuantity(1);

        Set<CreateOrderRequest.Item> items = new HashSet<>();
        items.add(item1Req);
        items.add(item2Req);
        request.setItems(items);

        OrderEntity savedEntity = new OrderEntity();
        savedEntity.setId(10L);
        savedEntity.setOrderNumber("ORD-2024-010");
        savedEntity.setUserId(user.getId());
        savedEntity.setItems(new HashSet<>()); // Para simplificar

        when(orderRepository.save(any(OrderEntity.class))).thenReturn(savedEntity);
        when(productClient.getProductById(product1.getId())).thenReturn(product1);
        when(productClient.getProductById(product2.getId())).thenReturn(product2);

        Order order = new Order();
        order.setId(savedEntity.getId());
        order.setOrderNumber(savedEntity.getOrderNumber());
        when(orderMapper.toDomain(savedEntity)).thenReturn(order);
        when(userClient.getUserById(user.getId())).thenReturn(user);

        Order result = orderService.register(request);

        assertNotNull(result);
        assertEquals("ORD-2024-010", result.getOrderNumber());
        assertEquals(user, result.getUser());
    }
