package com.orden.service.orden_service.service;

import com.orden.service.orden_service.client.Product;
import com.orden.service.orden_service.client.ProductClient;
import com.orden.service.orden_service.client.User;
import com.orden.service.orden_service.client.UserClient;
import com.orden.service.orden_service.dto.CreateOrderRequest;
import com.orden.service.orden_service.dto.Order;
import com.orden.service.orden_service.dto.OrderItem;
import com.orden.service.orden_service.entity.OrderEntity;
import com.orden.service.orden_service.entity.OrderItemEntity;
import com.orden.service.orden_service.mapper.OrderItemMapper;
import com.orden.service.orden_service.mapper.OrderMapper;
import com.orden.service.orden_service.repository.OrderRepository;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import org.mockito.MockitoAnnotations;

import java.math.BigDecimal;
import java.util.HashSet;
import java.util.Optional;
import java.util.Set;

import static org.junit.jupiter.api.Assertions.*;
import static org.mockito.Mockito.*;

class OrderServiceTest {

    @InjectMocks
    private OrderService orderService;

    @Mock
    private UserClient userClient;

    @Mock
    private ProductClient productClient;

    @Mock
    private OrderMapper orderMapper;

    @Mock
    private OrderItemMapper orderItemMapper;

    @Mock
    private OrderRepository orderRepository;

    private User mockUser;
    private Product mockProduct;

    @BeforeEach
    void setUp() {
        MockitoAnnotations.openMocks(this);

        mockUser = new User();
        mockUser.setId(10L);
        mockUser.setName("Test User");

        mockProduct = new Product();
        mockProduct.setId(100L);
        mockProduct.setName("Test Product");
        mockProduct.setPrice(BigDecimal.valueOf(50));

        // Lenient permite evitar errores por llamadas repetidas en Mockito 4+
        lenient().when(userClient.getUserById(anyLong())).thenReturn(mockUser);
        lenient().when(productClient.getProductById(anyLong())).thenReturn(mockProduct);
    }

    @Test
    void testRegisterOrderSuccess() {
        CreateOrderRequest request = new CreateOrderRequest();
        request.setUserId(10L);

        CreateOrderRequest.Item itemRequest = new CreateOrderRequest.Item();
        itemRequest.setProductId(100L);
        itemRequest.setQuantity(2);

        Set<CreateOrderRequest.Item> items = new HashSet<>();
        items.add(itemRequest);
        request.setItems(items);

        OrderEntity savedEntity = new OrderEntity();
        savedEntity.setId(1L);
        savedEntity.setUserId(10L);
        savedEntity.setOrderNumber("ORD-2024-010");
        savedEntity.setItems(new HashSet<>());
        savedEntity.setTotalAmount(BigDecimal.valueOf(100));

        OrderItemEntity orderItemEntity = new OrderItemEntity();
        orderItemEntity.setId(1L);
        orderItemEntity.setProductId(100L);
        orderItemEntity.setQuantity(2);
        orderItemEntity.setUnitPrice(BigDecimal.valueOf(50));
        orderItemEntity.setSubtotal(BigDecimal.valueOf(100));
        orderItemEntity.setOrderEntity(savedEntity);
        savedEntity.getItems().add(orderItemEntity);

        when(orderRepository.save(any(OrderEntity.class))).thenReturn(savedEntity);
        when(orderItemMapper.toDomainWithProduct(any(OrderItemEntity.class), any(Product.class)))
                .thenAnswer(invocation -> {
                    OrderItemEntity entity = invocation.getArgument(0);
                    Product product = invocation.getArgument(1);
                    OrderItem item = new OrderItem();
                    item.setProductId(entity.getProductId());
                    item.setQuantity(entity.getQuantity());
                    item.setUnitPrice(entity.getUnitPrice());
                    item.setSubtotal(entity.getSubtotal());
                    return item;
                });
        when(orderMapper.toDomain(any(OrderEntity.class))).thenAnswer(invocation -> {
            OrderEntity entity = invocation.getArgument(0);
            Order order = new Order();
            order.setUser(mockUser);
            order.setItems(new HashSet<>());
            order.setOrderNumber(entity.getOrderNumber());
            return order;
        });

        Order order = orderService.register(request);

        assertNotNull(order);
        assertEquals("ORD-2024-010", order.getOrderNumber());
        assertEquals(1, order.getItems().size());
        assertEquals(mockUser.getId(), order.getUser().getId());
    }

    @Test
    void testFindByIdMultipleItems() {
        OrderEntity orderEntity = new OrderEntity();
