@SpringBootTest
public class OrderServiceTest {

    @Mock
    private OrderRepository orderRepository;

    @Mock
    private OrderMapper orderMapper;

    @InjectMocks
    private OrderService orderService;

    private OrderEntity orderEntity;
    private Order orderDto;

    @BeforeEach
    public void setUp() {
        MockitoAnnotations.openMocks(this);

        orderEntity = new OrderEntity();
        orderEntity.setId(1L);
        orderEntity.setOrderNumber("ORD123");
        orderEntity.setUserId(10L);
        orderEntity.setStatus("NEW");
        orderEntity.setTotalAmount(new BigDecimal("100.0"));
        orderEntity.setCreatedAt(LocalDateTime.now());
        orderEntity.setUpdatedAt(LocalDateTime.now());

        orderDto = new Order();
        orderDto.setId(1L);
        orderDto.setOrderNumber("ORD123");
        orderDto.setStatus("NEW");
        orderDto.setTotalAmount(100.0);
        orderDto.setCreatedAt(LocalDateTime.now());
    }

    @Test
    public void testGetOrderById_Success() {
        when(orderRepository.findById(1L)).thenReturn(Optional.of(orderEntity));
        when(orderMapper.toDto(orderEntity)).thenReturn(orderDto);

        Order result = orderService.getOrderById(1L);

        assertNotNull(result);
        assertEquals("ORD123", result.getOrderNumber());
        assertEquals(100.0, result.getTotalAmount());
        verify(orderRepository, times(1)).findById(1L);
        verify(orderMapper, times(1)).toDto(orderEntity);
    }

    @Test
    public void testGetOrderById_NotFound() {
